import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { db, firebase } from '../firebase';
import Logo from '../img/logo.png';


export default class Home extends Component {
  
        state = {
            posts:[],
            inputValue:"",
            edit:false,
            id:"",
        }
    
    
    componentDidMount() {
      const posts=[];
         db.collection('userPosts').onSnapshot((snapShots) => {
            snapShots.docs.map(doc => {
                    
                    const userPost = (doc.data(), doc.id);

                    posts.push(userPost);
                    console.log("este es el state", this.state)

                });

                this.setState({
                    posts,
                    
                });
                console.log('este es el estado actualizado', this.state)
                
            })
            
        }
    
    // Función para Cerrar Sesión
    handleLogout () {
    firebase.auth().signOut()
    .then(result => console.log(`${result.user.email} ha cerrado sesión`))
    .catch(error => console.log(`Error ${error.code}: ${error.message}`));
}

    changeValue = (e) => {
        e.preventDefault();
        this.setState({
            inputValue:e.target.value
        })
    };

    savePost = () => {
        const { inputValue, edit } = this.state;
        !edit ?
        db.collection("userPosts").add({
            post:inputValue
        }).then(()=>{
            console.log('agregado');
        }).catch(()=>{
            console.log('error')
        }) :
        this.update();
        this.setState({
            inputValue:"",
        })
    }

    getPosts= () => {
        console.log('entramos al getpost');
    //     let docRef = db.collection("userPosts").doc(id);
    //     console.log();
    //     // docRef.get().then((doc)=>{
    //     //     if(doc.exists){
    //     //         this.setState({
    //     //             inputValue:doc.data().post,
    //     //             edit:true,
    //     //             id:doc.id
    //     //         })
    //     //     }else {
    //     //         console.log('El documento no existe')
    //     //     }
    //     // }).catch((error)=> {
    //     //     console.log(error);
    //     // })
     }
    
    update = () => {
        const { id, inputValue } = this.state;
        db.collection("userPosts").doc(id).update({
            item: inputValue
        }).then(() => {
            console.log('actualizado');
        }).catch((error) => {
            console.log(error);
        })
    } 
    render() {
        return(
            <fragment>
                <div className='container'>
                 <img className="imgLogo" alt="logo" src={Logo} />
                 <div className="createPost">
                     <h5>Realizar una Nueva Publicación</h5>
                     <input type="text" value={this.state.inputValue} onChange={this.changeValue}/>
                     <button className="bnt" onClick={this.savePost}>Publicar</button>
                 </div>
                 <div className="showPost">
                     {this.state.posts.map((post, key)=>{
                         return (
                             <div key={key}>
                                 <h4 className="white-text">{post.post}</h4>
                                 <button className="btn">Editar</button>
                                 <button className="btn">Eliminar</button>
                             </div>
                         )
                     })}
                 
                 </div>
                
    
                <Link to='/'>
                <button className="btn" onClick={this.handleLogout}>Cerrar Sesión</button>
                </Link>
               
            </div>  
            </fragment>
            
        );
    }

}
