import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { db, firebase } from '../firebase';
import M from "materialize-css";
import Juego from '../img/miJuego.jpg';

const styles= {
    navBar: {
        paddingLeft: '5%',
        paddingRight: '5%'
    },
    createPost: {
        borderRadius: '25px',
        border: '2px, solid, red',
        padding: '20px',
        paddingButton:'10%',
        width: '1000px',
        height: '30px'
      },
      btnPublic: {
        marginTop:'2%'
      }
}
export default class Home extends Component {
  
        state = {
            posts:[],
            inputValue:"",
            edit:false,
            id:"",
            notification:false,
            message:"",
        }
    
    
        componentDidMount() {
            
                // Auto initialize all the things!
                M.AutoInit();
            
            db.collection("userPosts").onSnapshot((snapShots) => {
                this.setState({
                    posts:snapShots.docs.map(doc => {
                        return {id:doc.id, data:doc.data()}
                    })
                })
            })
        }
    // Funci贸n para Cerrar Sesi贸n
    handleLogout () {
    firebase.auth().signOut()
    .then(result => console.log(`${result.user.email} ha cerrado sesi贸n`))
    .catch(error => console.log(`Error ${error.code}: ${error.message}`));
}
    //Funci贸n que actualiza el estado para colocarle el valor que tiene el input de comentario
    changeValue = (e) => {
        this.setState({
            inputValue:e.target.value
        })
    };
    // funci贸n que permite guardar el post nuevo en la colecci贸n de firebase
    savePost = () => {
        const { inputValue, edit } = this.state;
        !edit ?
        db.collection("userPosts").add({
            post:inputValue
        }).then(()=>{
            this.message('agregado');
        }).catch(()=>{
            this.message('error')
        }) :
        this.update();
        this.setState({
         inputValue:"",
        })
    }
    //Funci贸n que toma el id del documento, y  muestra el documento en el input para que pueda ser editado
    getPosts= (id) => {
        let docRef = db.collection("userPosts").doc(id);
        docRef.get().then((doc)=>{
            if(doc.exists){
                this.setState({
                    inputValue:doc.data().post,
                    edit:true,
                    id:doc.id
                })
            }else {
                console.log('El documento no existe')
            }
        }).catch((error)=> {
            console.log(error);
        })
     }
    //Funci贸n que actualiza el post editado
    update = () => {
        const { id, inputValue } = this.state;
        db.collection("userPosts").doc(id).update({
            post: inputValue
        }).then(() => {
            this.message('actualizado');
        }).catch((error) => {
            this.message('error');
        })
    } 

    //Funci贸n que elimina el post seleccionado
    deletePost = (id) => {
        db.collection("userPosts").doc(id).delete();
    }
    message = (message) => {
        this.setState({
            notification:true,
            message: message
        })
    }
    render() {
        const { posts, inputValue, notification, message } = this.state;
        return(
            <fragment>
                <nav>
                            <div style={styles.navBar} className="navBar nav-wrapper orange">
                            <a href="#" className="brand-logo black-text">SP</a>
                            <ul id="nav-mobile" className="right hide-on-med-and-down">
                                <li><a href="sass.html"className='black-text'><strong>Inicio</strong></a></li>
                                <li><a href="badges.html" className='black-text'><strong>Mi Perfil</strong></a></li>
                               
                                <li><Link to='/'> <a onClick={this.handleLogout} className='black-text'><strong>Cerrar Sesi贸n</strong></a></Link></li>
                                 
                                
                            </ul>
                            </div>
                        </nav>
                        <div class="parallax-container">
                    <div class="parallax"><img src={Juego}/></div>
                    </div>
                <div className='container'>
                    
                  
                      <div className="createPost">
                     <h5 className="orange-text"><strong>Realizar una Nueva Publicaci贸n</strong></h5>
                     <input style={styles.createPost} type="texto" className="white-text" value={inputValue} onChange={this.changeValue}/> 
                     <button style={styles.btnPublic}  className="btn orange" onClick={this.savePost}>Publicar</button>
                     </div>
                    
               
                 <div className="showPost">
                     {posts && posts !== undefined ? posts.map((post, key)=>{
                         return (
                             <div key={key}>
                                 <h4 className="white-text">{post.data.post}</h4>
                                 <button className="btn orange" onClick={() => this.getPosts(post.id)}>Editar</button>
                                 <button className="btn orange" onClick={() => this.deletePost(post.id)}>Eliminar</button>
                                  
                             </div>
                         )
                     }): null}
                 
                 </div>  
               
            </div>  
            
            </fragment>
            
        );
    }

}
