import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { db, firebase } from '../firebase';
import Logo from '../img/logo.png';


export default class Home extends Component {

    state = {
        posts :[]
    }
    componentDidMount(){
        db.collection('userPosts').get().then((snapShots)=>{
            this.setState({
                posts:snapShots.docs.map(doc=> {
                    return { id:doc.id, data:doc.data()};
                })
            })
        }), error=>{
            console.log(error);
        }
    }
    
    // Función para Cerrar Sesión
    handleLogout () {
    firebase.auth().signOut()
    .then(result => console.log(`${result.user.email} ha cerrado sesión`))
    .catch(error => console.log(`Error ${error.code}: ${error.message}`));
}
    
    render() {
        const { posts } = this.state;
        return(
            <div className='container'>
                 <img className="imgLogo" alt="logo" src={Logo} />
                
                { posts && posts !== undefined ? posts.map((post, key) => (
                    <div className="userPost">
                    <ul key={key}></ul>
                    <h4>{post.data.post}</h4>
                    <button className="bnt">Editar</button>
                    <button className="btn">Eliminar</button>
                    </div>
                )
                

        ):null}

                
                <Link to='/'>
                <button className="btn" onClick={this.handleLogout}>Cerrar Sesión</button>
                </Link>
               
            </div>
        );
    }
}
