import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { db, firebase } from '../firebase';
import Logo from '../img/logo.png';


export default class Home extends Component {
  
        state = {
            posts:[],
            inputValue:"",
        }
    
    
    componentDidMount() {
      const posts=[];
         db.collection('userPosts').onSnapshot((snapShots) => {
            snapShots.docs.map(doc => {
                    
                    const userPost = (doc.data());
                    posts.push(userPost);
                    console.log("este es el state", this.state)

                });

                this.setState({
                    posts,
                });
                console.log('este es el estado actualizado', this.state)
                
            })
            
        }
    
    // Función para Cerrar Sesión
    handleLogout () {
    firebase.auth().signOut()
    .then(result => console.log(`${result.user.email} ha cerrado sesión`))
    .catch(error => console.log(`Error ${error.code}: ${error.message}`));
}

    changeValue = (e) => {
        this.setState({
            inputValue:e.target.value
        })
    };

    publicPost = () => {
        const { inputValue } = this.state;
        db.collection("userPosts").add({
            post:inputValue
        }).then(()=>{
            console.log('agregado');
        }).catch(()=>{
            console.log('error')
        })
    }
    
    render() {
        return(
            <fragment>
                <div className='container'>
                 <img className="imgLogo" alt="logo" src={Logo} />
                 <div className="createPost">
                     <h5>Realizar una Nueva Publicación</h5>
                     <input type="text" value={this.state.inputValue} onChange={this.changeValue}/>
                     <button className="bnt" onClick={this.publicPost}>Publicar</button>
                 </div>
                 <div className="showPost">
                     {this.state.posts.map((post, key)=>{
                         return (
                             <div key={key}>
                                 <h4 className="white-text">{post.post}</h4>
                             </div>
                         )
                     })}
                 
                 </div>
                
    
                <Link to='/'>
                <button className="btn" onClick={this.handleLogout}>Cerrar Sesión</button>
                </Link>
               
            </div>  
            </fragment>
            
        );
    }

}
