import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { db, firebase } from '../firebase';
import Logo from '../img/logo.png';


export default class Home extends Component {
  
        state = {
            posts:[],
            inputValue:"",
            edit:false,
            id:"",
        }
    
    
        componentDidMount() {
            db.collection("userPosts").onSnapshot((snapShots) => {
                this.setState({
                    posts:snapShots.docs.map(doc => {
                        return {id:doc.id, data:doc.data()}
                    })
                })
            })
        }
    // Función para Cerrar Sesión
    handleLogout () {
    firebase.auth().signOut()
    .then(result => console.log(`${result.user.email} ha cerrado sesión`))
    .catch(error => console.log(`Error ${error.code}: ${error.message}`));
}
    //Función que actualiza el estado para colocarle el valor que tiene el input de comentario
    changeValue = (e) => {
        this.setState({
            inputValue:e.target.value
        })
    };
    // función que permite guardar el post nuevo en la colección de firebase
    savePost = () => {
        const { inputValue, edit } = this.state;
        !edit ?
        db.collection("userPosts").add({
            post:inputValue
        }).then(()=>{
            console.log('agregado');
        }).catch(()=>{
            console.log('error')
        }) :
        this.update();
        this.setState({
         inputValue:"",
        })
    }
    //Función que toma el id del documento, y  muestra el documento en el input para que pueda ser editado
    getPosts= (id) => {
        let docRef = db.collection("userPosts").doc(id);
        docRef.get().then((doc)=>{
            if(doc.exists){
                this.setState({
                    inputValue:doc.data().post,
                    edit:true,
                    id:doc.id
                })
            }else {
                console.log('El documento no existe')
            }
        }).catch((error)=> {
            console.log(error);
        })
     }
    //Función que actualiza el post editado
    update = () => {
        const { id, inputValue } = this.state;
        db.collection("userPosts").doc(id).update({
            post: inputValue
        }).then(() => {
            console.log('actualizado');
        }).catch((error) => {
            console.log(error);
        })
    } 

    //Función que elimina el post seleccionado
    deletePost = () => {
        db.collection("userPosts").doc(id).delete()
    }
    render() {
        const { posts, inputValue } = this.state;
        return(
            <fragment>
                <div className='container'>
                 <img className="imgLogo" alt="logo" src={Logo} />
                 <div className="createPost">
                     <h5 className="white-text">Realizar una Nueva Publicación</h5>
                     <input type="text" value={inputValue} onChange={this.changeValue}/>
                     <button className="bnt" onClick={this.savePost}>Publicar</button>
                 </div>
                 <div className="showPost">
                     {posts && posts !== undefined ? posts.map((post, key)=>{
                         return (
                             <div key={key}>
                                 <h4 className="white-text">{post.data.post}</h4>
                                 <button className="btn" onClick={() => this.getPosts(post.id)}>Editar</button>
                                 <button className="btn" onClick={() => this.deletePost(post.id)}>Eliminar</button>
                             </div>
                         )
                     }): null}
                 
                 </div>
                
    
                <Link to='/'>
                <button className="btn" onClick={this.handleLogout}>Cerrar Sesión</button>
                </Link>
               
            </div>  
            </fragment>
            
        );
    }

}
