import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import { firebase } from '../firebase';
import Logo from '../img/logo.png';
import Brujo from '../img/brujo.png';

const styles = {
    brujo: {
      paddingLeft:'37%',
      paddingTop:'5%'
    },
    input: {
        borderRadius: '25px',
        border: '2px, solid, red',
        padding: '20px',
        width: '300px',
        height: '10px'
      },
        mail: {
          paddingRight: '13%'
      },
      password: {
          paddingRight:'18%',
          paddingTop:'1%'
      },
      imgLogo: {
          maxWidth:'600px',
          paddingLeft:'40%'
      }
      
  
}


 export default class Login extends Component {
    // constructor() {
    //     super();
    //     this.state = {
    //         user: null
    //     };
        
    //     this.handleAuth=this.handleAuth.bind(this);
    //     this.handleLogout=this.handleLogout.bind(this);
    // }
    // /* Mediante este método verificamos si el user tiene algún contenido y si tiene contenido actualizamos el estado
    // y el componente se vuelve a renderizar*/ 
    
    // componentWillMount () {
    //     firebase.auth().onAuthStateChanged(user => {
    //         this.setState ({ user });   
    //     })
    // }
    // Función para iniciar Sesión
    handleAuth () {
        const provider = new firebase.auth.GoogleAuthProvider();

        firebase.auth().signInWithPopup(provider)
        .then(result => console.log(`${result.user.email} ha iniciado sesión`))
        .catch(error => console.log(`Error ${error.code}: ${error.message}`));
    }
    
    // Función que verifica si el usuario esta logueado o no
    
    render () {
        return(
                  <div className="loginPage container row section">
                     
                      <img style={styles.imgLogo} alt="logo" src={Logo} />  
                <div className='col s12 m12 center'>
                <h6 style={styles.mail} className="orange-text"><strong>Correo Electrónico:</strong></h6>
                <input style={styles.input} type='texto' className="login grey darken-3" id="correo" required />
                <h6 style={styles.password} className="orange-text"><strong>Contraseña:</strong></h6>
                <input style={styles.input} type='texto' className="password grey darken-3" id="contraseña" required />
                </div>
                
                <Link to='/Home'>
                     <img style={styles.brujo} className="brujo center" alt="brujo" src={Brujo} />
                     <div style={styles.brujo}>
                     <button className="button btn orange">Iniciar Sesión</button> 
                     <button className="button btn orange" onClick={this.handleAuth}>Login con Google</button>  
                     </div>
                </Link>  
            </div>
           
          
        );
    }
}
